import React, { useState, useEffect } from 'react';

function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await fetch('http://ecommerce.muersolutions.com/api/v1/products');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }

    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Product List</h1>
      <div className="product-list">
        {products.map(product => (
          <div key={product.created} className="product-card">
            <img src={product.product_thumbnail} alt={product.product_name} />
            <h2>{product.product_name}</h2>
            <p>{product.product_description}</p>
            <p>Price: ${product.unit_price / 100}</p>
            <p>Ranking: {product.ranking}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
