import React, { useState, useEffect } from 'react';
import Cart from './Cart';

const Homepage = () => {
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Replace the URL with your actual API endpoint
    fetch('http://ecommerce.muersolutions.com/api/v1/products')
      .then((response) => response.json())
      .then((data) => setProducts(data));
  }, []);

  const addToCart = (product, index) => {
    // Modify the addToCart function to add the product name and price to the cart
    setCartItems([...cartItems, { id: index, name: product.product_name, price: product.unit_price }]);
  };

  return (
    <div className="homepage">
      <h2>Welcome to Our Store</h2>
      <div className="products">
        {products.map((product, index) => (
          <div key={index} className="product-card">
            <img src={product.product_thumbnail} alt={product.product_name} />
            <div>
              <h3>{product.product_name}</h3>
              <p>{product.product_description}</p>
              <p>Price: ${product.unit_price}</p>
            </div>
            <button onClick={() => addToCart(product, index)}>Add to Cart</button>
          </div>
        ))}
      </div>
      <Cart cartItems={cartItems} />
    </div>
  );
};

export default Homepage;
