import React, { useState, useEffect } from 'react';

function Home() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await fetch('https://api.escuelajs.co/api/v1/products');
        const data = await response.json();
        setProducts(data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }

    fetchProducts();
  }, []);

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleImageChange = (direction) => {
    const newIndex = currentImageIndex + direction;
    if (newIndex >= 0 && newIndex < products.length) {
      setCurrentImageIndex(newIndex);
    }
  };

  return (
    <div>
      <h1>Product List</h1>
      <div className="product-list">
        {products.map((product, index) => (
          <div key={product._id} className="product-card">
            <h2>{product.product_name}</h2>
            <p>{product.product_description}</p>
            <p>Price: ${product.unit_price / 100}</p>
            <div className="product-images">
              {product.product_images.map((image, imageIndex) => (
                <img
                  key={imageIndex}
                  src={image}
                  alt={`Product ${index + 1} - Image ${imageIndex + 1}`}
                  className={currentImageIndex === imageIndex ? 'active' : ''}
                />
              ))}
            </div>
            <div className="image-navigation">
              <button onClick={() => handleImageChange(-1)} disabled={currentImageIndex === 0}>
                Previous
              </button>
              <button
                onClick={() => handleImageChange(1)}
                disabled={currentImageIndex === product.product_images.length - 1}
              >
                Next
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
